import { useState } from "react";
import Header from "../../component/Header";
import ItemList from "../../component/List";

import {
  Arroba,
  ButaoDeBusca,
  Descrição,
  DivContent,
  HomeBackGround,
  HomeContent,
  Input,
  Nome,
  Perfil,
  ProfileImage,
  ProfileInfo,
  RepoDiv,
  Title,
} from "./Styles";

const App = () => {
  const [user, setUser] = useState("");
  const [currentUser, setCurrentUser] = useState(null);
  const [repos, setRepos] = useState(null)


  const handleGetData = async () => {
    const userData = await fetch(`https://api.github.com/users/${user}`);
    const newUser = await userData.json();

    console.log(newUser)

    if(newUser.name) {
      const {avatar_url, name, bio} = newUser

      setCurrentUser({avatar_url,name, bio})
    }

    const reposData = await fetch(`https://api.github.com/users/${user}/repos`);
    const newRepos = await reposData.json

    if(newRepos.length) {
      setRepos(newRepos)

    }
  }

  return (
    <>
      <Header />
      <HomeContent>
        <HomeBackGround src="./git.png" alt="img" />
        <DivContent>
          <div style={{ display: "flex" }}>
            <Input name="usuario" placeholder="Escreva o nome do usuario" value={user} onChange={(e) => setUser(e.target.value)}/>
            <ButaoDeBusca onClick={handleGetData}>Buscar</ButaoDeBusca>
          </div>
          {currentUser?.name ? (
            <>
            <ProfileInfo>
              <ProfileImage src="./git.png" />
              <Perfil>
                <Nome>{currentUser.name}</Nome>
                <Arroba>@Azaro</Arroba>
                <Descrição>Descrição</Descrição>
              </Perfil>
           </ProfileInfo>
           <hr style={{ width: "90%" }} />
           </>
          ) : null}


          <RepoDiv>
            <Title>Repositorios</Title>
            {repos?.lenght ? (
              <>
              <ItemList title="test1" description="teste de descricao" />
              <ItemList title="test2" description="teste de descricao" />
              <ItemList title="test3" description="teste de descricao" />
              </>

            ): null}

          </RepoDiv>
        </DivContent>
      </HomeContent>
    </>
  );
};

export default App;
